"use strict";function leftPad(t){return 100>t?"0"+t:t}function clipToRange(t){return Math.round(t/255*127)+128}var baseURL="http://cnnr.local/arduino/set/";$(document).mousemove(function(t){t.preventDefault();var e=$(this).width(),n=$(this).height(),o=Math.round(t.clientX/e*255),u=Math.round(t.clientY/n*40)+30;$(document.body).css("backgroundColor","hsl("+o+",100%,"+u+"%)")}),$(document).click(function(t){t.preventDefault();var e=$(document.body).css("backgroundColor"),n=e.split(","),o=n[0].substring(4),u=n[1],r=n[2].length>1?n[2].substring(0,n[2].length-1):n[2];$.ajax({url:baseURL+leftPad(clipToRange(Number(o)))+"/"+leftPad(clipToRange(Number(u)))+"/"+leftPad(clipToRange(Number(r)))+"/"})});